interface VoiceInputProps {
  onTranscriptChange: (transcript: string) => void;
  onGenerateMandala: (transcript: string) => void;
  isGenerating: boolean;
  resetTrigger?: number; // Increment to trigger reset
}

export function VoiceInput({ onTranscriptChange, onGenerateMandala, isGenerating, resetTrigger }: VoiceInputProps) {
  const {
    isListening,
    transcript,
    startListening,
    stopListening,
    resetTranscript,
    isSupported,
    error
  } = useSpeechRecognition();

  const [hasStartedRecording, setHasStartedRecording] = useState(false);
  const [manualText, setManualText] = useState('');
  const [isManualMode, setIsManualMode] = useState(false);

  // Effective transcript combines speech recognition and manual text
  const effectiveTranscript = isManualMode ? manualText : transcript;

  // Update parent component when transcript changes
  useEffect(() => {
    onTranscriptChange(effectiveTranscript);
  }, [effectiveTranscript, onTranscriptChange]);

  // Handle external reset trigger
  useEffect(() => {
    if (resetTrigger && resetTrigger > 0) {
      // Stop listening if currently active
      if (isListening) {
        stopListening();
      }
      // Clear all transcript data
      resetTranscript();
      setManualText('');
      setHasStartedRecording(false);
      setIsManualMode(false);
    }
  }, [resetTrigger, isListening, stopListening, resetTranscript]);

  const handleToggleRecording = () => {
    if (isListening) {
      stopListening();
    } else {
      if (!hasStartedRecording) {
        setHasStartedRecording(true);
        resetTranscript();
      }
      startListening();
    }
  };

  const handleGenerateMandala = () => {
    if (effectiveTranscript.trim()) {
      onGenerateMandala(effectiveTranscript.trim());
      onTranscriptChange(effectiveTranscript.trim());
    }
  };

  const handleClearTranscript = () => {
    resetTranscript();
    setManualText('');
    onTranscriptChange('');
    setHasStartedRecording(false);
    setIsManualMode(false);
  };

  const handleManualTextChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setManualText(e.target.value);
    setIsManualMode(true);
  };

  const toggleManualMode = () => {
    setIsManualMode(!isManualMode);
    if (!isManualMode) {
      setManualText(transcript || '');
    } else {
      resetTranscript();
    }
  };

  if (!isSupported) {
    return (
      <Card className="glass border-border/50">
        <CardContent className="p-6">
          <h3 className="text-lg font-semibold mb-4 flex items-center">
            <MicOff className="text-destructive mr-3 h-5 w-5" />
            Voice Input
          </h3>
          <div className="text-center py-8">
            <p className="text-muted-foreground" data-testid="text-unsupported-browser">
              Speech recognition is not supported in your browser. Please use Chrome, Edge, or Safari.
            </p>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="glass border-border/50">
      <CardContent className="p-6">
        <h3 className="text-lg font-semibold mb-4 flex items-center">
          <Mic className="text-secondary mr-3 h-5 w-5" />
          Voice Input
        </h3>

        <div className="space-y-4">
          {/* Recording Button */}
          <div className="text-center">
            <Button
              onClick={handleToggleRecording}
              disabled={isGenerating}
              className={`w-16 h-16 sm:w-20 sm:h-20 rounded-full mx-auto mb-3 sm:mb-4 transition-all duration-200 touch-manipulation ${
                isListening 
                  ? 'bg-destructive hover:bg-destructive/80 animate-pulse' 
                  : 'bg-gradient-to-r from-secondary to-accent hover:scale-105 active:scale-95'
              }`}
              data-testid="button-toggle-recording"
            >
              {isListening ? (
                <Square className="h-6 w-6 sm:h-8 sm:w-8 text-primary-foreground" />
              ) : (
                <Mic className="h-6 w-6 sm:h-8 sm:w-8 text-primary-foreground" />
              )}
            </Button>
            <p className="text-sm text-muted-foreground px-4" data-testid="text-recording-instruction">
              {isListening ? 'Tap to stop recording' : 'Tap to start speaking'}
            </p>
          </div>

          {/* Voice Transcript / Manual Input */}
          <div className="bg-secondary/30 rounded-lg p-4 min-h-[120px] relative">
            <div className="flex items-center justify-between mb-2">
              <div className="text-sm text-muted-foreground">
                {isManualMode ? 'Texto Manual:' : 'Transcript:'}
              </div>
              <Button
                onClick={toggleManualMode}
                size="sm"
                variant="ghost"
                className="h-6 px-2 text-xs"
                data-testid="button-toggle-manual-mode"
              >
                <Edit3 className="w-3 h-3 mr-1" />
                {isManualMode ? 'Voz' : 'Texto'}
              </Button>
            </div>

            {isManualMode ? (
              <textarea
                value={manualText}
                onChange={handleManualTextChange}
                className="w-full min-h-[60px] bg-transparent border-none outline-none resize-none text-foreground"
                placeholder="Digite seus pensamentos, sentimentos ou intenÃ§Ãµes..."
                data-testid="textarea-manual-input"
              />
            ) : (
              <div 
                className="text-foreground min-h-[60px] whitespace-pre-wrap"
                data-testid="text-transcript"
              >
                {transcript || (hasStartedRecording ? 'Listening...' : 'Click the microphone to start speaking')}
              </div>
            )}

            {isListening && !isManualMode && (
              <div className="flex items-center mt-3 space-x-2">
                <div className="w-2 h-2 bg-red-500 rounded-full animate-pulse" />
                <span className="text-xs text-muted-foreground" data-testid="text-listening-indicator">
                  Listening...
                </span>
              </div>
            )}

            {error && !isManualMode && (
              <div className="mt-2 text-sm text-destructive" data-testid="text-speech-error">
                {error}
                <div className="text-xs text-muted-foreground mt-1">
                  Clique no botÃ£o "Texto" acima para inserir texto manualmente
                </div>
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-3">
            <Button
              onClick={handleGenerateMandala}
              disabled={!effectiveTranscript.trim() || isGenerating || (isListening && !isManualMode)}
              className="flex-1 bg-gradient-to-r from-primary to-secondary hover:from-primary/80 hover:to-secondary/80 touch-manipulation min-h-[44px]"
              data-testid="button-generate-mandala"
            >
              {isGenerating ? (
                <>
                  <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                  Generating...
                </>
              ) : (
                <>
                  <div className="w-4 h-4 mr-2">âœ¨</div>
                  Generate Mandala
                </>
              )}
            </Button>

            {effectiveTranscript && (
              <Button
                onClick={handleClearTranscript}
                disabled={isGenerating || (isListening && !isManualMode)}
                variant="outline"
                className="w-full sm:w-auto touch-manipulation min-h-[44px]"
                data-testid="button-clear-transcript"
              >
                Clear
              </Button>
            )}
          </div>

          {/* Tips */}
          {!hasStartedRecording && (
            <div className="bg-primary/10 border border-primary/20 rounded-lg p-3">
              <p className="text-xs text-muted-foreground" data-testid="text-voice-tips">
                ðŸ’¡ <strong>Tips:</strong> Speak about your feelings, thoughts, or intentions. 
                The AI will combine your words with your brain waves to create a unique mandala.
              </p>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

import { useState, useEffect, useRef, useCallback } from 'react';

interface SpeechRecognitionEvent extends Event {
  results: SpeechRecognitionResultList;
  resultIndex: number;
}

interface SpeechRecognitionErrorEvent extends Event {
  error: string;
}

export interface UseSpeechRecognitionReturn {
  isListening: boolean;
  transcript: string;
  startListening: () => void;
  stopListening: () => void;
  resetTranscript: () => void;
  isSupported: boolean;
  error: string | null;
}

export function useSpeechRecognition(): UseSpeechRecognitionReturn {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const [error, setError] = useState<string | null>(null);
  const recognitionRef = useRef<any>(null);

  // Check if speech recognition is supported
  const isSupported = typeof window !== 'undefined' && 
    ('SpeechRecognition' in window || 'webkitSpeechRecognition' in window);

  useEffect(() => {
    if (!isSupported) {
      setError('Speech recognition is not supported in this browser');
      return;
    }

    // Initialize speech recognition
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    const recognition = new SpeechRecognition();

    recognition.continuous = true;
    recognition.interimResults = true;
    recognition.lang = 'pt-BR';

    recognition.onstart = () => {
      setIsListening(true);
      setError(null);
    };

    recognition.onresult = (event: SpeechRecognitionEvent) => {
      let finalTranscript = '';
      let interimTranscript = '';

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) {
          finalTranscript += transcript;
        } else {
          interimTranscript += transcript;
        }
      }

      setTranscript(prev => {
        const newTranscript = prev + finalTranscript;
        return newTranscript;
      });
    };

    recognition.onerror = (event: SpeechRecognitionErrorEvent) => {
      setError(`Speech recognition error: ${event.error}`);
      setIsListening(false);
    };

    recognition.onend = () => {
      setIsListening(false);
    };

    recognitionRef.current = recognition;

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, [isSupported]);

  const startListening = useCallback(() => {
    if (!isSupported) {
      setError('Speech recognition is not supported');
      return;
    }

    if (recognitionRef.current && !isListening) {
      try {
        recognitionRef.current.start();
      } catch (error) {
        setError('Failed to start speech recognition');
        console.error('Speech recognition start error:', error);
      }
    }
  }, [isListening, isSupported]);

  const stopListening = useCallback(() => {
    if (recognitionRef.current && isListening) {
      recognitionRef.current.stop();
    }
  }, [isListening]);

  const resetTranscript = useCallback(() => {
    setTranscript('');
    setError(null);
  }, []);

  return {
    isListening,
    transcript,
    startListening,
    stopListening,
    resetTranscript,
    isSupported,
    error
  };
}

// Extend the Window interface to include speech recognition
declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

import { useEffect, useRef, useState, useCallback } from 'react';
import { BrainwaveData } from '@shared/schema';

export interface WebSocketMessage {
  type: string;
  data?: any;
  error?: string;
  message?: string;
}

export interface UseWebSocketReturn {
  isConnected: boolean;
  sendMessage: (message: any) => void;
  lastMessage: WebSocketMessage | null;
  eegData: BrainwaveData | null;
  neuroskyConnected: boolean;
  connectNeuroSky: () => void;
  disconnectNeuroSky: () => void;
}

export function useWebSocket(): UseWebSocketReturn {
  const ws = useRef<WebSocket | null>(null);
  const [isConnected, setIsConnected] = useState(false);
  const [lastMessage, setLastMessage] = useState<WebSocketMessage | null>(null);
  const [eegData, setEegData] = useState<BrainwaveData | null>(null);
  const [neuroskyConnected, setNeuroskyConnected] = useState(false);

  const connect = useCallback(() => {
    try {
      const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
      const wsUrl = `${protocol}//${window.location.host}/ws`;

      ws.current = new WebSocket(wsUrl);

      ws.current.onopen = () => {
        console.log('WebSocket connected');
        setIsConnected(true);
      };

      ws.current.onmessage = (event) => {
        try {
          const message: WebSocketMessage = JSON.parse(event.data);
          setLastMessage(message);

          switch (message.type) {
            case 'eeg_data':
              if (message.data) {
                setEegData(message.data);
              }
              break;
            case 'neurosky_connected':
              setNeuroskyConnected(true);
              break;
            case 'neurosky_disconnected':
              setNeuroskyConnected(false);
              break;
            case 'connection_status':
              setNeuroskyConnected(message.data?.connected || false);
              if (message.data?.currentData) {
                setEegData(message.data.currentData);
              }
              break;
            case 'neurosky_error':
              console.error('NeuroSky error:', message.error);
              break;
          }
        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      };

      ws.current.onclose = () => {
        console.log('WebSocket disconnected');
        setIsConnected(false);
        setNeuroskyConnected(false);

        // Attempt to reconnect after 3 seconds
        setTimeout(() => {
          if (!ws.current || ws.current.readyState === WebSocket.CLOSED) {
            connect();
          }
        }, 3000);
      };

      ws.current.onerror = (error) => {
        console.error('WebSocket error:', error);
      };

    } catch (error) {
      console.error('Failed to connect WebSocket:', error);
    }
  }, []);

  const sendMessage = useCallback((message: any) => {
    if (ws.current && ws.current.readyState === WebSocket.OPEN) {
      ws.current.send(JSON.stringify(message));
    } else {
      console.warn('WebSocket is not connected');
    }
  }, []);

  const connectNeuroSky = useCallback(() => {
    sendMessage({ type: 'connect_neurosky' });
  }, [sendMessage]);

  const disconnectNeuroSky = useCallback(() => {
    sendMessage({ type: 'disconnect_neurosky' });
  }, [sendMessage]);

  useEffect(() => {
    connect();

    return () => {
      if (ws.current) {
        ws.current.close();
      }
    };
  }, [connect]);

  return {
    isConnected,
    sendMessage,
    lastMessage,
    eegData,
    neuroskyConnected,
    connectNeuroSky,
    disconnectNeuroSky
  };
}

import { QueryClient, QueryFunction } from "@tanstack/react-query";

async function throwIfResNotOk(res: Response) {
  if (!res.ok) {
    const text = (await res.text()) || res.statusText;
    throw new Error(`${res.status}: ${text}`);
  }
}

export async function apiRequest(
  method: string,
  url: string,
  data?: unknown | undefined,
): Promise<Response> {
  const res = await fetch(url, {
    method,
    headers: data ? { "Content-Type": "application/json" } : {},
    body: data ? JSON.stringify(data) : undefined,
    credentials: "include",
  });

  await throwIfResNotOk(res);
  return res;
}

type UnauthorizedBehavior = "returnNull" | "throw";
export const getQueryFn: <T>(options: {
  on401: UnauthorizedBehavior;
}) => QueryFunction<T> =
  ({ on401: unauthorizedBehavior }) =>
  async ({ queryKey }) => {
    const res = await fetch(queryKey.join("/") as string, {
      credentials: "include",
    });

    if (unauthorizedBehavior === "returnNull" && res.status === 401) {
      return null;
    }

    await throwIfResNotOk(res);
    return await res.json();
  };

export const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      queryFn: getQueryFn({ on401: "throw" }),
      refetchInterval: false,
      refetchOnWindowFocus: false,
      staleTime: Infinity,
      retry: false,
    },
    mutations: {
      retry: false,
    },
  },
});

import { clsx, type ClassValue } from "clsx"
import { twMerge } from "tailwind-merge"

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs))
}

